import { useState, useEffect } from 'react';
import RedichanNav from 'RedichanNav';
import 'bootstrap/dist/css/bootstrap.min.css';
import Stack from 'react-bootstrap/Stack';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import consola from 'consola';
import imageThumbnail from 'image-thumbnail';
/* import Container from 'react-bootstrap/Container'; */
/* import Row from 'react-bootstrap/Row'; */
/* import Col from 'react-bootstrap/Col'; */

interface Props {
  name: string;
}

interface Thread {
  id: number;
  poster: string;
  zeroPostTexts: Array<string>;
  attachmentPath: string;
  postNumber: number;
  UTCTimestamp: Date;
}

const getImageThumbnail = (path: string): Blob => {
  const options = { width: 100, height: 100, fit: 'cover' };
  return imageThumbnail(path, options);
}

const getVideoThumbnail = (path: string) => {
  return new Promise((resolve) => {
